import React, { useState } from 'react';
import axios from 'axios';
import { Form } from 'react-bootstrap';
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import DUMMY_DATA from './data';
import '../src/css/App.css';

function MessageRow(message, index) {
  return (
    <div className="text-box" key={index}>
      <div className="message" >{message.text}</div>
      <div className="user-name" >{message.senderId}</div>
    </div>
  )
}

function Chat() {

  const [writtenText, setWrittenText] = useState(DUMMY_DATA);

  async function handleSubmit(e) {
    const response = await axios.get('https://jsonplaceholder.typicode.com/todos/1');
    console.log(response.data);
  }

  return (
    <Container className="page-container">
      <h1>VOCO</h1>
      <div className="chat-container" id='1234'>
        <div className="messages-box">
          {writtenText.map((message, index) => MessageRow(message, index))}
        </div>

        <Form>

          <Form.Group className='mb-3'>
            <Form.Control></Form.Control>
          </Form.Group>
          <Form.Group className='mb-3'>
            <Form.Control></Form.Control>
          </Form.Group>
          <Button onClick={e => handleSubmit(e)}>Send</Button>
        </Form>
      </div>

    </Container>
  );
}

export default Chat;
